pragma solidity ^0.4.23;

import "openzeppelin-solidity/contracts/token/ERC721/ERC721BasicToken.sol";
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";

contract PersonaToken is ERC721BasicToken, Ownable {

    using SafeMath for uint256;

    struct Persona {
        // The genetic makeup of the Persona. Core deterministic feature.
        uint256 code;

        // The name of the persona
        string name;
    }

    Persona[] personas;
    mapping (address => uint256) internal ownerToPersona;
    // TODO: Doesn't need to be uint256?
    uint256 nonce = 0;

    event NewPersona(
        address indexed _to,
        string _name,
        uint256 indexed _personaId
    );

    modifier isNewUser() {
        require(ownedTokensCount[msg.sender] == 0);
        _;
    }

    function _createPersona(string _name, uint256 _personaCode) 
        internal 
        returns (uint256 personaId) 
    {
        Persona memory newPersona = Persona({name: _name, code: _personaCode});
        personaId = personas.push(newPersona) - 1;
        _mint(msg.sender, personaId);
        ownerToPersona[msg.sender] = personaId;
        emit NewPersona(msg.sender, _name, personaId);
    }

    function createPersona(string _name)
        external
        isNewUser
        returns (uint256 personaId)
    {
        // TODO: Obviously don't make the code the nonce
        personaId = _createPersona(_name, nonce);
        nonce = nonce.add(1);
    }

    function getPersonaId(address _owner)
        external
        view
        returns (uint256 personaId)
    {
        require(_owner != address(0));
        personaId = ownerToPersona[_owner];
    }

}
